mapper.update:
UPDATE arrival a
LEFT JOIN product b on b.productSeq = a.productSeqF
SET
 	a.arrivalAccount = #{arrivalAccount}
 	,b.productName = #{productName}
 	,b.productPrice = #{productPrice}
 WHERE a.arrivalSeq = #{arrivalSeq}

mapper.delete:
DELETE FROM  arrival
WHERE  1=1
	AND arrivalSeq = #{arrivalSeq}
