mapper.update:
		update member a
		left join company b
		on b.companySeq = a.companySeqF
		set
			a.memberName = #{memberName}
            ,a.memberId = #{memberId}
            ,a.memberPwd = #{memberPwd}
         	,a.phoneNumber = #{phoneNumber}
         	,a.memberTypeCd = #{memberTypeCd}
         	,a.companySeqF = #{companySeqF}
    		where 1=1
    		and a.memberSeq = #{memberSeq}

mapper.delete:
delete from member
where 1 = 1
	and memberSeq = #{memberSeq}
