mapper.update:
		update member a
		left join company b
		on b.memberSeqF = a.memberSeq
		set
			memberName = #{memberName}
            ,memberId = #{memberId}
            ,memberPwd = #{memberPwd}
         	,phoneNumber = #{phoneNumber}
         	,memberTypeCd = #{memberTypeCd}
         	,companyName = #{companyName}
    		where 1=1
    		and memberSeq = #{memberSeq}

mapper.delete:
delete from member
where 1 = 1
	and memberSeq = #{memberSeq}
