mapper.sql:
      from
		orderProduct b
	left join product c
		on c.productSeq = b.productSeqF
	left join member d
		on d.memberSeq = b.orderSeqF
	Where 1=1 

        AND b.orderProductDelNy = #{shDelny}


					AND b.orderProductRegDate BETWEEN #{shDateStart} AND #{shDateEnd}
					AND b.orderProductModDate BETWEEN #{shDateStart} AND #{shDateEnd}



            AND a.codeGroupSeqF = #{shValue}
            AND a.codedSeq = #{shValue}
            AND a.codeName LIKE CONCAT('%',#{shValue},'%')

mapper.update:
UPDATE orderProduct 
SET
orderAccount = #{orderAccount}
WHERE 
	orderProductSeq= #{orderProductSeq}
